function SVG($k){
	global $svg_list;
	$a=$svg_list[$k];
	$a['css']=str_replace('cls-',$k.'_',$a['css']);
	$a['svg']=str_replace('cls-',$k.'_',$a['svg']);
	return '<svg xmlns="http://www.w3.org/2000/svg" class="'.$k.'" viewBox="'.$a['box'].'">'.$a['css'].$a['svg'].'</svg>';
}

